import {Component} from 'react'
import styles from './styles.scss'

export default class Icon extends Component {
  constructor() {
    super()
    this.state = {
      isActived: false
    }
  }

  getPath() {
    const {path} = this.props
    const {isActived} = this.state
    return  `/static/icon/${path}_${isActived?'d': 's'}.png` 
  }

  render() {
    return <div>
      <img src={this.getPath()} />
      {this.props.path}
    </div>
  }
}

Icon.defaultProps = {
  path: ''
}
