import {Component} from 'react'
import styles from './styles.scss'

export default class Icon extends Component {
  constructor() {
    super()
    this.state = {
      isActived: false
    }
  }

  getPath() {
    const {path} = this.props
    const {isActived} = this.state
    return  `/static/icon/${path}_${isActived? 's': 'd'}.png` 
  }

  getActiveTextClass() {
    return this.state.isActived ? 'actived': '';
  }

  render() {
    return <div className={styles.icon}>
      <img className={styles.img} src={this.getPath()} />
      <span className={[styles.text, this.getActiveTextClass()].join(' ')}>{this.props.path}</span>
    </div>
  }
}

Icon.defaultProps = {
  path: ''
}
